import numpy as np

array = np.arange(0, 6)
array2 = np.arange(6, 12)

print("Array 1:",array)
print("Array 2:",array2)

print("addition of Two Array:",array + array2)   
print("Subtraction of Two Array:",array2 - array)
print("Multiplication of Two Array:",array * array2)
print("Division of Two Array:",array / array2)
